ALTER TABLE licenciamento.tipo_documento ADD COLUMN id_usuario_licenciamento INTEGER;
ALTER TABLE licenciamento.tipo_documento ADD COLUMN data_cadastro timestamp without time zone;
ALTER TABLE licenciamento.tipo_documento ADD COLUMN ativo BOOLEAN DEFAULT 'true';

COMMENT ON COLUMN licenciamento.tipo_documento.id_usuario_licenciamento IS 'Identificador único da entidade usuario_licenciamento.';
COMMENT ON COLUMN licenciamento.tipo_documento.data_cadastro IS 'Data de cadastro do documento.';
COMMENT ON COLUMN licenciamento.tipo_documento.ativo IS 'Determina se um item documento está ativo ou inativo';

DELETE FROM licenciamento.tipo_documento a
    where exists (select 1 from licenciamento.tipo_documento b
        WHERE a.nome=b.nome
        AND a.id <> b.id
        AND (select count(*) from licenciamento.documento where id_tipo_documento = a.id) = 0
        AND (select count(*) from licenciamento.rel_tipo_licenca_grupo_documento where id_tipo_documento = a.id) = 0
        AND (select count(*) from licenciamento.tipo_documento_tipo_licenca where id_tipo_documento = a.id) = 0);

ALTER TABLE licenciamento.tipo_documento ADD CONSTRAINT uq_nome_tipo_documento UNIQUE (nome);


